INSERT INTO authors (name, birth_date, biography)
VALUES
    ('Hamid Olimjon', '1956-07-15', 'Hamid Olimjon - oʻzbek sheʼriyotshunos, adabiyotshunos, falsafiy, diniy fikrlar muxlisidir. Uning adabiyotida inson, hayot va diniy mavzular muhim oʻrin egallaydi.'),
    ('Erkin Vohidov', '1963-04-22', 'Erkin Vohidov - oʻzbek yozuvchisi, sheʼriyotshunos va dramaturg. Uning eserlari ommalashgan, milliy va jahon adabiyotlarida iz oʻstirgan.'),
    ('Tohir Malik', '1950-09-30', 'Tohir Malik - oʻzbek yozuvchisi, adabiyotshunos va ilmiy faol. Uning ilmiy va adabiy faoliyati oʻzbek adabiyoti tarixida jiddiy oʻrni egallagan.'),
    ('Gʻafur Gulom', '1903-05-15', 'Gʻafur Gulom - oʻzbek yozuvchisi, sheʼriyotshunos, adabiyotshunos va ilmiy faol. Uning ijodida oʻzbek xalq sheʼriyatining oʻziga xos zavqlari va qobiliyatlari jamiyatga ifodalangan.'),
    ('Alisher Navoiy', '1441-01-09', 'Alisher Navoiy - oʻzbek yozuvchisi, adib, mutafakkir, dostonshunos, filolog, adabiyotshunos, dostonshunos, mufassir, ilmiy, dramaturg, insonshunos, filolog, mutafakkir.'),
    ('Abdulla Oripov', '1935-06-06', 'Abdulla Oripov - oʻzbek yozuvchisi, dramaturg, sheʼriyotshunos, mutafakkir.'),
    ('Abdulla Qodriy', '1894-11-27', 'Abdulla Qodiriy — oʻzbek yozuvchisi, oʻzbek milliy poeti, dostonshunos, pedagog. U "Tong nafasi", "Oʻzgina hayot" va "Oʻtgan kunlar" dastanlarini yaratgan.'),
    ('Zahiriddin Muhammad Bobur', '1483-02-14', 'Zahiriddin Muhammad Bobur — oʻzbek yozuvchisi, shoir, badiiy asarlar muallifi va davlat boshqaruvchisi, Timur xon (Teymuridlar hanligining oʻn birinchi vosiyosi) farzandlari oilasidan birinchi xon. "Boburnoma" asarining muallifi.'),
    ('Muhammad Yusuf', '1854-04-26', 'Muhammad Yusuf — oʻzbek yozuvchisi, dramaturg, sheʼriyotshunos, adabiyotshunos, ilmiy faol. "Uchrashuv", "Uch oq", "Suyurgali yoʻldoshlar" kabi asarlari bilan mashhur.'),
    ('Xudoyberdi Toʼxtaboyev', '1932-12-17', 'Xudoyberdi Toʼxtaboyev — taniqli oʼzbek yozuvchisi va adibi. Uning asarlari oʼzbek bolalar adabiyoti va umuman oʼzbek adabiyoti uchun muhim ahamiyatga ega. Toʼxtaboyevning asarlari ko`pincha yoshlar va bolalar uchun moʼljallangan boʼlib, ularning koʼpchiligi hayotiy, maʼrifiy va axloqiy mavzularni qamrab oladi. Uning eng mashhur asarlari orasida "Sariq devni minib" va "Shirin qovunlar mamlakati" bor.');
